import { useState } from "react";
import './App.css';

import React from "react";
import Breakfast from "./components/Breakfast";
import Lunch from "./components/Lunch";
import Shakes from "./components/Shakes";
import items from "./components/Data"

import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link
} from "react-router-dom";

const allCategories = ["all", ...new Set(items.map((item) => item.category))]
function App() {
  const [data, setData] = useState(items);


  const filterItem = (category) => {
    if (category === "all") {
      setData(items);
    } else {
      const filteredData = items.filter((e) => e.category === category)
      setData(filteredData);
    }
  };

  return (

    <Router>
      <div>
        <nav>
          <h1>Menu</h1>
          <ul className='list'>
            <li>
              <Link to="/">Breakfast</Link>
            </li>
            <li>
              <Link to="/about">Lunch</Link>
            </li>
            <li>
              <Link to="/users">Shakes</Link>
            </li>
          </ul>
        </nav>

        {/* A <Switch> looks through its children <Route>s and
            renders the first one that matches the current URL. */}
        <Routes>
          <Route path="/" element={<Breakfast />} />
          <Route path="/about" element={<Lunch />} />
          <Route path="/users" element={<Shakes />} />
        </Routes>

      </div>
    </Router>


  );
}



export default App;
